sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var reqDate = new Date(newValue);
	var today = new Date();

	if(reqDate.getDate() < today.getDate()) {

		g_form.setValue("u_when_needed", null);
		g_form.showFieldMsg("u_when_needed", "La date ne doit pas être dans le passé !", "error");

	}
	if(reqDate.getDate() == today.getDate()) {

		g_form.setValue("u_when_needed", null);
		g_form.showFieldMsg("u_when_needed", "Vous ne pouvez pas soumettre de demandes NeedIt pour aujourd'hui !", "error");	
	}
}
